  render() {
    return (
      <>
        <nav className="navbar navbar-expand-lg navbar-dark bg-primary sticky-top">
          <p>Score: 0 | Top Score: 0</p>
        </nav>
        <div class="jumbotron">
          <h1 class="display-3">
            Welcome to the critically acclaimed Clicky Game!!
          </h1>
